'        where label = ''Mobile''',
'            ), ''-'' ) > 0 then',
'        ',
'            ''fa-phone''',
'            ',
'            else',
'               ''No Data''',
'    end phone_ico,',
'',
'   ( select target',
'        from table( AF_ASSIGNMENT_PCK.get_call_menu_table( unit.code ) )',
'        where label = ''Mobile''',
'   ) oncall_mobile,',
'   ',
'   /*',
'   case ',
'   ',
'       when ( user_profile_image.ID is null or NVL(dbms_lob.getlength(user_profile_image.content),0) = 0 or mime_type not like ''image%'' )                ',
'          then ',
'',
'                ''<span class="t-Icon fa fa-extra-large fa-user fa_ppl_extra" ''',
'             || ''      style="font-size:60px;"> ''',
'             || ''</span>''     ',
'',
'       else ',
'                ''<span class="t-Icon fa fa-extra-large " ''',
'             || ''      style="font-size:75px; display:inline-block;"> ''',
'             || ''   <img class="img-sq" src="'' ',
'             || APEX_UTIL.GET_BLOB_FILE_SRC (p_item_name => ''P15_CONTENT'',p_v1 => user_profile_image.ID,p_v2 => null,p_content_disposition => ''inline'') ',
'             || ''" height="60px" width="60px"/> ''',
'             || ''</span>'' ',
'',
'     end USER_PROFILE_IMAGE_URL,',
'*/',
'',
'    case  when ( user_profile_image.ID is null or NVL(dbms_lob.getlength(user_profile_image.content),0) = 0 or mime_type not like ''image%'' )                ',
'          then ',
'            APEX_UTIL.GET_BLOB_FILE_SRC (p_item_name => ''P15_CONTENT'',p_v1 => -999,p_v2 => null,p_content_disposition => ''inline'') ',
'       else ',
'            APEX_UTIL.GET_BLOB_FILE_SRC (p_item_name => ''P15_CONTENT'',p_v1 => user_profile_image.ID,p_v2 => null,p_content_disposition => ''inline'') ',
'     end USER_PROFILE_IMAGE_URL_SRC,',
'   ',
'     ',
'     case when sec_info.assgn_sections is null then ''Section not assigned''',
'        --  else ''<b>( '' || af_section.code || '' ) '' || af_section.name || ''</b>''',
'          else ''<b>'' || sec_info.assgn_sections || ''</b>''',
'     end section_name,',
'     ',
'     case when div_info.assgn_divisions is null then ''Division not assigned''',
'        --  else ''<b>( '' || af_division.code || '' ) '' ||  af_division.name || ''</b>''',
'          else ''<b>'' || div_info.assgn_divisions || ''</b>''',
'     end division_name,',
'    ',
'     case when fav.unit_code is null ',
'       --   then ''<span onClick="$.event.trigger(''''update_unit_fav'''', [['''''' || unit.code || '''''', ''''insert'''']]);" class="t-Icon fa fa-heart-o unit_fav" aria-hidden="true" style="text-shadow: 0px 4px 10px gray; font-size:15px; display:inline-block;"></'
||'span>''    ',
'       --   else ''<span onClick="$.event.trigger(''''update_unit_fav'''', [['''''' || unit.code || '''''', ''''delete'''']]);" class="t-Icon fa fa-heart unit_fav" aria-hidden="true" style="text-shadow: 0px 4px 10px gray; font-size:15px; display:inline-block;"></sp'
||'an>'' ',
'          then ''<span data-id="'' || unit.code || ''" data-action="insert" class="t-Icon fa fa-heart-o unit_fav" aria-hidden="true" style="text-shadow: 0px 4px 10px gray; font-size:15px; display:inline-block;"></span>''    ',
'          else ''<span data-id="'' || unit.code || ''" data-action="delete" class="t-Icon fa fa-heart unit_fav" aria-hidden="true" style="text-shadow: 0px 4px 10px gray; font-size:15px; display:inline-block;"></span>'' ',
'     ',
'     end fav_icon,',
'     ',
'     '''' next_page,',
'     ',
'     '''' next_page_for_favorite',
'',
'from af_unit unit',
'',
'left join af_section_unit_mapping',
'on unit.code = af_section_unit_mapping.unit_code',
'',
'left join',
'( ',
'    select b.*',
'    from',
'    (',
'        select ',
'            unit_code,',
'            max( effective_datetime ) max_datetime',
'        from af_oncall_schedule',
'        where effective_datetime <= systimestamp',
'        group by unit_code',
'    ) a',
'    inner join af_oncall_schedule b',
'    on a.unit_code = b.unit_code and a.max_datetime = b.effective_datetime',
') sche',
'on unit.code = sche.unit_code',
'',
'left join users',
'on upper( users.username ) = upper( sche.assignee_username ) ',
'',
'left join',
'(',
'    select b.code, a.username from af_user_favorites a',
'      inner join AF_unit b',
'      on a.unit_code = b.code',
'      where upper( a.username ) = upper( :CURRENT_USERNAME )',
') favorite_user_units',
'on unit.code = favorite_user_units.code',
'',
'left join',
'(',
'    select distinct unit_code, :CURRENT_USERNAME username from af_section_unit_mapping where section_code in',
'    (',
'        select a.section_code from af_section_unit_mapping a',
'          inner join AF_unit b',
'          on a.unit_code = b.code',
'          inner join ( select * from users where upper( username ) = upper( :CURRENT_USERNAME ) ) users',
'          on upper( users.unit ) = upper( b.business_name ) ',
'    )',
'      ',
') section_user_units',
'on unit.code = section_user_units.unit_code',
'',
'left join',
'(',
'    ',
'    select distinct b_1.unit_code, :CURRENT_USERNAME username ',
'    from af_division_section_mapping a',
'    inner join af_section_unit_mapping b ',
'    on a.section_code = b.section_code ',
'    inner join af_division_section_mapping a_1 ',
'    on a_1.division_code = a.division_code ',
'    inner join af_section_unit_mapping b_1 ',
'    on a_1.section_code = b_1.section_code',
'    where b.section_code in',
'    (',
'        ',
'        select section_code from af_section_unit_mapping where section_code in',
'        (',
'            select a.section_code from af_section_unit_mapping a',
'              inner join AF_unit b',
'              on a.unit_code = b.code',
'              inner join ( select * from users where upper( username ) = upper( :CURRENT_USERNAME ) ) users',
