'      ',
') division_user_units',
'on unit.code = division_user_units.unit_code',
'',
'left join',
'(',
'    select unit_code from AF_user_favorites where upper( username ) = upper( :CURRENT_USERNAME )',
') fav',
'on unit.code = fav.unit_code',
'',
'left join ',
'(',
'    select ',
'        unit_code,',
'        LISTAGG( name, chr(10) ) WITHIN GROUP (ORDER BY name) assgn_sections',
'    from',
'    (',
'        select',
'           distinct sec_unit.unit_code, b.name',
'        from af_section_unit_mapping sec_unit',
'        inner join af_section b',
'        on sec_unit.section_code = b.code',
'    ) a',
'    group by unit_code',
') sec_info',
'on unit.code = sec_info.unit_code',
'',
'left join ',
'(',
'    select ',
'        unit_code,',
'        LISTAGG( name, chr(10) ) WITHIN GROUP (ORDER BY name) assgn_divisions',
'    from',
'    (',
'        select',
'           distinct sec_unit.unit_code, af_division.name',
'        from af_section_unit_mapping sec_unit',
'        inner join af_division_section_mapping div_sec',
'        on sec_unit.section_code = div_sec.section_code',
'        inner join af_division',
'        on af_division.code = div_sec.division_code',
'    ) a',
'    group by unit_code',
') div_info',
'on unit.code = div_info.unit_code',
'',
' ',
'left join user_profile_image',
'on upper( AF_ASSIGNMENT_PCK.ret_oncall_pass_onto_info( sche.unit_code, ''pass_onto'' ) ) = upper(user_profile_image.username)',
'and user_profile_image.image_type = ''USER_PROFILE''',
'',
'where unit.on_call = ''Y'' and active = ''Y''',
'and',
'(',
'     (',
'         :P200_SEARCH is null',
'         and',
'         upper( :P200_MODE ) = upper( ''all'' )',
'         and',
'         1 = 1',
'',
'     )',
'    or',
