'select ',
'   ',
'     sec.id,',
' --    setting.setting_1 color_code,',
' --    setting.setting_2 padding,',
'     AF_APP_CONFIG_PCK.getFlexibleSeetingValue( ''af_section'', sec.code, ''SETTING_1'' ) color_code,',
'     AF_APP_CONFIG_PCK.getFlexibleSeetingValue( ''af_section'', sec.code, ''SETTING_2'' ) padding,',
'     sec.shortname shortname,',
'     sec.name || case when sec_unit_map.cnt is null then null when sec_unit_map.cnt = 1 then '' [ 1 unit ]'' else '' [ '' || sec_unit_map.cnt || '' units ]'' end name,',
'     ',
'     case when map.alre_assned_div_names is null then ''not assigned''',
'          else ''<b>'' || map.alre_assned_div_names || ''</b>''',
'     end div_name,',
'     ',
'     sec.description,',
'     sec.code,',
'     sec.seq_id,',
'     '''' next_page',
'',
'from af_section sec',
'left join ',
'(',
'    select a.section_code, ',
'           LISTAGG( b.name, chr(10) ) WITHIN GROUP (ORDER BY b.name) alre_assned_div_names,',
'           LISTAGG( b.code, chr(10) ) WITHIN GROUP (ORDER BY b.name) alre_assned_div_codes',
'    from af_division_section_mapping a',
'    inner join af_division b',
'    on a.division_code = b.code',
'    group by a.section_code',
') map',
'on sec.code = map.section_code',
'',
'left join',
'(',
'    select section_code, count(*) cnt ',
'    from af_section_unit_mapping a',
'    inner join af_unit b',
'    on a.unit_code = b.code',
'    where b.active = ''Y'' and on_call = ''Y''',
'    group by section_code',
') sec_unit_map',
'on sec.code = sec_unit_map.section_code',
'',
'/*',
'left join af_flexible_settings setting',
'on setting.key_2 = sec.code',
'and setting.key_1 = ''af_section''',
'*/',
'',
'where ',
'(',
'    :P500_MODE is null',
'    or',
'    :P500_MODE = ''all''',
'    or',
'    (',
'        :P500_MODE = ''specific_division''',
'        and',
'        (',
'            instr( map.alre_assned_div_codes, chr(10) || :P500_MODE_PARAM ) > 0',
'            or',
'            instr( map.alre_assned_div_codes, :P500_MODE_PARAM || chr(10) ) > 0',
'            or ',
'            map.alre_assned_div_codes = :P500_MODE_PARAM',
'        )',
'        ',
'    )',
')',
'order by seq_id',
''))
